import React from 'react';
import { useState } from 'react';
import Data from './MOCK_DATA.json'

const SearchBar = () => {
    const [query, setQuery] = useState('')
    return <div>
    <input type="text" placeholder="Search" onChange={event => setQuery(event.target.value)} />
    <div>
    {
            Data.filter(user => {
              if (query === '') {
                return '';
              } else if (user.first_name.toLowerCase().includes(query.toLowerCase())) {
                return user;
              }
            }).map((user, index) => (
              <div key={index}>
                <p>{user.first_name}</p>
                <p>{user.last_name}</p>
                <p>{user.email}</p>
              </div>
            ))
            }
    </div>
    </div>

        };

export default SearchBar;
