import React from 'react';

const data = [
  {
    number: 1,
    institution: "University of California, Los Angeles",
    affiliation: "Public"
  },
  {
    number: 2,
    institution: "Stanford University",
    affiliation: "Private"
  },
  {
    number: 3,
    institution: "Harvard University",
    affiliation: "Private"
  },
  {
    number: 4,
    institution: "Massachusetts Institute of Technology",
    affiliation: "Private"
  },
  {
    number: 5,
    institution: "California Institute of Technology",
    affiliation: "Private"
  }
];



const Table = () => {
  const [selectedRow, setSelectedRow] = useState(null);

  const handleRowClick = (row) => {
    setSelectedRow(row);
  };

  return (
    <div style={{ overflow: "auto", maxHeight: "400px" }}>
      <table>
        <thead>
          <tr>
            <th>Number</th>
            <th>Institutions</th>
            <th>Affiliations</th>
          </tr>
        </thead>
        <tbody>
          {data.map((row, index) => (
            <tr
              key={index}
              style={{ backgroundColor: selectedRow === row ? "yellow" : "" }}
              onClick={() => handleRowClick(row)}
            >
              <td>{row.number}</td>
              <td>{row.institution}</td>
              <td>{row.affiliation}</td>
            </tr>
          ))}
        </tbody>
      </table>
      {selectedRow && (
        <div>
          <p>Number: {selectedRow.number}</p>
          <p>Institution: {selectedRow.institution}</p>
          <p>Affiliation: {selectedRow.affiliation}</p>
        </div>
      )}
    </div>
  );
};


const InstitutionsandAffiliates = () => (

  <div style={{backgroundColor: "white", height: "100vh", display: "flex", justifyContent: "center", alignItems: "center"}}>
    <h1>Institutions and Affiliates</h1>
  </div>
);

export default InstitutionsandAffiliates;
