import React, { useState } from "react";
import './styling/InstitutionsandAffiliates.css';

const institutionsData = [
  {
    number: 1,
    institution: "University of California, Los Angeles",
    affiliation: "Public"
  },
  {
    number: 2,
    institution: "Stanford University",
    affiliation: "Private"
  },
  {
    number: 3,
    institution: "Harvard University",
    affiliation: "Private"
  },
  {
    number: 4,
    institution: "Massachusetts Institute of Technology",
    affiliation: "Private"
  },
  {
    number: 5,
    institution: "California Institute of Technology",
    affiliation: "Private"
  }
];


const InstitutionsandAffiliates = () => {
  const [selectedInstitution, setSelectedInstitution] = useState(null);

  const handleRowClick = (institution) => {
    setSelectedInstitution(institution);
  };

  return (
    <div className="institutionsContainer">
      <table className="institutionsTable">
        <thead>
          <tr>
            <th>Number</th>
            <th>Institution</th>
            <th>Affiliation</th>
          </tr>
        </thead>
        <tbody>
          {institutionsData.map((institution) => (
            <tr key={institution.id} onClick={() => handleRowClick(institution)}>
              <td>{institution.number}</td>
              <td>{institution.name}</td>
              <td>{institution.affiliation}</td>
            </tr>
          ))}
        </tbody>
      </table>
      {selectedInstitution && (
        <div className="modalBackdrop">
          <div className="modal">
            <div className="modalHeader">
              <h2>{selectedInstitution.name}</h2>
              <button onClick={closeModal} className="closeButton">X</button>
            </div>
            <div className="modalContent">
              <p><strong>Affiliation:</strong> {selectedInstitution.affiliation}</p>
              <p><strong>Number:</strong> {selectedInstitution.number}</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );


};


export default InstitutionsandAffiliates;
