                return '';
              } else if (user.first_name.toLowerCase().includes(query.toLowerCase())) {
                return user;
              }
            }).map((user, index) => (
              <div key={index}>
                <p>{user.first_name}</p>
                <p>{user.last_name}</p>
                <p>{user.email}</p>
              </div>
            ))
    }
  </div>
);

